package com.sourabh;

public class UseofBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{
			System.out.println(i);
			
			if(i==5) break; //break stops the loop execution when the specified condition is met
			
			//break is also used when you want to stop a loop where you write it.
		}
	}

}
